mapper.update:
update t_rele
set rele_user_id = #{releUserId,jdbcType=INTEGER},
  rele_task_id = #{releTaskId,jdbcType=INTEGER},
  rele_type = #{releType,jdbcType=CHAR}
where rele_id = #{releId,jdbcType=INTEGER}

mapper.select:
SELECT
COUNT(*)
FROM t_rele r
WHERE r.rele_task_id=#{taskId}
AND r.rele_user_id=#{userId}

SELECT
COUNT(*)
FROM t_rele r
WHERE r.rele_task_id=#{taskId}
AND r.rele_user_id=#{userId}
AND r.rele_type=#{releTypeBeClaim}

SELECT COUNT(*)
FROM t_rele r
WHERE r.rele_user_id=#{userId}
AND r.rele_task_id=#{taskId}
AND r.rele_type=#{releTypeSelfSelect}

mapper.update:
UPDATE t_rele r
SET r.rele_type=#{releTypeBeClaim}
WHERE r.rele_user_id=#{userId}
AND r.rele_task_id=#{taskId};
